use iThings;

create table sys_module_api
(
    id            bigint auto_increment
        primary key,
    module_code   varchar(50)                              not null,
    route         varchar(100)                             not null,
    method        varchar(50)                              not null,
    name          varchar(100)                             not null,
    business_type bigint                                   not null,
    `group`       varchar(100)                             not null,
    is_need_auth  bigint      default 1                    not null,
    `desc`        varchar(500)                             not null,
    created_time  datetime(3) default current_timestamp(3) not null,
    updated_time  datetime(3) default current_timestamp(3) not null,
    deleted_time  bigint unsigned                          null,
    created_by    bigint                                   null,
    deleted_by    bigint                                   null,
    updated_by    bigint                                   null,
    constraint app_route
        unique (module_code, route, deleted_time)
)
    charset = utf8mb3;

create index idx_sys_module_api_created_time
    on sys_module_api (created_time);

